<?phpclass BoxController extends BaseController {    public function createBox() {        $degrees = Degree::all();        if(count($degrees) == 0) {            $msg = 'پایه ی تحصیلی ای برای ایجاد جعبه ی جدید وجود ندارد';            return View::make('home', array('msg' => $msg));        }        return View::make('createBox', array('degrees' => $degrees));    }        public function seeBoxes() {        if(isset($_POST["editSelectedBox"])) {            $boxId = makeValidInput($_POST["editSelectedBox"]);            try {                $degrees = Degree::all();                $box = Box::find($boxId);                $sId = BoxItems::where("boxId", "=", $box->id)->first()->subject_id;                $dId = getDegreeOfSubject($sId);                $lId = Subject::where("id", "=", $sId)->select("id_l")->first()->id_l;                return View::make('showBoxes', array('mode' => 'edit', 'selectedDegree' => $dId, 'selectedLesson' => $lId, 'degrees' => $degrees, 'box' => $box, 'msg' => ''));            }            catch (Exception $e) {            }        }        $boxes = Box::all();        return View::make('showBoxes', array('mode' => 'see', 'boxes' => $boxes));    }}