<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'Box' * * @property integer $id * @property integer $from_ * @property integer $to_ * @property string $name * @method static \Illuminate\Database\Query\Builder|\App\models\Box whereName($value) */class Box extends Model {    protected $table = 'box';    public $timestamps = false;    public static function whereId($target) {        return Box::find($target);    }    public function items() {        return $this->hasMany('App\models\BoxItems', 'box_id', 'id');    }}