<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'BoxItems' * * @property integer $id * @property integer $box_id * @property integer $subject_id * @property integer $grade * @method static \Illuminate\Database\Query\Builder|\App\models\BoxItems whereBoxId($value) * @method static \Illuminate\Database\Query\Builder|\App\models\BoxItems whereSubjectId($value) * @method static \Illuminate\Database\Query\Builder|\App\models\BoxItems whereCompassId($value) */class BoxItems extends Model {    protected $table = "box_items";    public $timestamps = false;    public static function whereId($target) {        return BoxItems::find($target);    }        public function box() {        return $this->belongsTo('App\models\Box', 'box_id', 'id');    }}