<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'City' * * @property integer $id * @property integer $state_id * @property string $name * @method static \Illuminate\Database\Query\Builder|\App\models\City whereStateId($value) */class City extends Model {        protected $table = 'cities';    public $timestamps = false;    public static function whereId($target) {        return City::find($target);    }    public function state() {        return $this->belongsTo('App\models\State', 'state_id', 'id');    }}