<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'Degree' * * @property integer $id * @property string $dN */class Degree extends Model {    protected $table = 'degree';    public $timestamps = false;    public static function whereId($target) {        return Degree::find($target);    }    public function quizes() {        return $this->belongsToMany('App\models\Quiz', 'degree_of_quiz', 'degree_id', 'quiz_id');    }    public function lessons() {        return $this->hasMany('App\models\Lesson', 'degree', 'id');    }    public function subjects() {        return $this->hasManyThrough('App\models\Subject', 'App\models\Lesson', 'degree', 'id_l');    }}