<?phpclass Degree extends Eloquent {    protected $table = 'degree';    public $timestamps = false;    public function quizes() {        return $this->belongsToMany('Quiz', 'degreeOfQuiz', 'degreeId', 'quizId');    }    public function lessons() {        return $this->hasMany('Lesson', 'degree', 'id');    }    public function subjects() {        return $this->hasManyThrough('Subject', 'Lesson', 'degree', 'id_l');    }}