<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'Degree' * * @property integer $id * @property string $dN */class Degree extends Model {    protected $table = 'degree';    public $timestamps = false;    public static function whereId($target) {        return Degree::whereId($target);    }    public function quizes() {        return $this->belongsToMany('Quiz', 'degreeOfQuiz', 'degree_id', 'quiz_id');    }    public function lessons() {        return $this->hasMany('Lesson', 'degree', 'id');    }    public function subjects() {        return $this->hasManyThrough('Subject', 'Lesson', 'degree', 'id_l');    }}