<?phpclass Lesson extends Eloquent {    protected $table = 'lessons';    public $timestamps = false;    public function degree() {        return $this->belongsTo('Degree', 'degree', 'id');    }    public function subjects() {        return $this->hasMany('Subject', 'id_l', 'id');    }    public function questions() {        return $this->hasManyThrough('Question', 'Subject', 'id_l', 'subject_id');    }}