<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\DB;/** * An Eloquent Model: 'Question' * * @property integer $id * @property integer $id_s * @property integer $subject_id * @property integer $grade * @property integer $status * @property integer $attempt * @property integer $solved * @property integer $id_err * @property integer $kind_bq * @property integer $controller_id * @property integer $organization_id * @property integer $compass_id * @property integer $from_ * @property integer $to_ * @property boolean $publicity * @property boolean $kind_s * @property boolean $kind_q * @property string $author * @property string $ans * @method static \Illuminate\Database\Query\Builder|\App\models\Question whereSubjectId($value) * @method static \Illuminate\Database\Query\Builder|\App\models\Question whereStatus($value) * @method static \Illuminate\Database\Query\Builder|\App\models\Question whereControllerId($value) * @method static \Illuminate\Database\Query\Builder|\App\models\Question whereOrganizationId($value) */class Question extends Model {    protected $table = 'questions';    public $timestamps = false;    public static function whereId($target) {        return Question::find($target);    }    public function subject() {        return $this->belongsTo('App\models\Subject', 'subject_id', 'id');    }    public function mqf() {        return $this->belongsTo('App\models\MQF', 'id', 'q_id');    }    public function msf() {        return $this->belongsTo('App\models\MSF', 'id', 'q_id');    }    public function mst() {        return $this->belongsTo('App\models\MST', 'id', 'q_id');    }    public function mqt() {        return $this->belongsTo('App\models\MQT', 'id', 'q_id');    }    public function mct() {        return $this->belongsTo('App\models\MCT', 'id', 'q_id');    }}