<?phpclass Question extends Eloquent {    protected $table = "questions";    public $timestamps = false;    public function subject() {        return $this->belongsTo('Subject', 'subject_id', 'id');    }    public function mqf() {        return $this->belongsTo('MQF', 'id', 'qId');    }    public function msf() {        return $this->belongsTo('MSF', 'id', 'qId');    }    public function mst() {        return $this->belongsTo('MST', 'id', 'qId');    }    public function mqt() {        return $this->belongsTo('MQT', 'id', 'qId');    }    public function mct() {        return $this->belongsTo('MCT', 'id', 'qId');    }    }