<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'Quiz' * * @property integer $id * @property string $QN * @property string $sDate * @property string $eDate * @property string $sTime * @property string $eTime * @property string $guide * @property integer $tL * @property integer $mark * @property boolean $minusMark * @property integer $author * @property integer $kindQ */class Quiz extends Model {    protected $table = "quiz";    public $timestamps = false;    public function degree() {        return $this->belongsToMany('Degree', 'degreeOfQuiz', 'quiz_id', 'degreeId');    }    public function boxes() {        return $this->belongsToMany('Box', 'boxesOfQuiz', 'quiz_id', 'box_id');    }    public static function whereId($target) {        return Quiz::whereId($target);    }}