<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;/** * An Eloquent Model: 'Quiz' * * @property integer $id * @property string $QN * @property string $sDate * @property string $eDate * @property string $sTime * @property string $eTime * @property integer $tL * @property integer $price * @property integer $mark * @property boolean $minusMark * @property string $author * @property integer $kindQ */class Quiz extends Model {    protected $table = "quiz";    public $timestamps = false;    public function degree() {        return $this->belongsToMany('App\models\Degree', 'degree_of_quiz', 'quiz_id', 'degree_id');    }    public function boxes() {        return $this->belongsToMany('App\models\Box', 'boxes_of_quiz', 'quiz_id', 'box_id');    }    public static function whereId($target) {        return Quiz::find($target);    }}