<?phpnamespace App\models;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\DB;/** * An Eloquent Model: 'Subject' * * @property integer $id * @property integer $id_l * @property integer $numQ * @property string $nameSubject * @method static \Illuminate\Database\Query\Builder|\App\models\Subject whereIdL($value) */class Subject extends Model {    protected $table = 'subjects';    public $timestamps = false;    public static function whereId($target) {        return Subject::whereId($target);    }        public function lesson() {        return $this->belongsTo('Lesson', 'id_l', 'id');    }    public function questions() {        return $this->hasMany('Question', 'subject_id', 'id');    }}