                            ?>                            <td><center style="direction: ltr">{{$itr->percent}}</center></td>                        @endforeach                        @if($sumCoherence != 0)                            <td><center style="direction: ltr">{{round(($sumLesson / $sumCoherence), 0)}}</center></td>                            <td><center style="direction: ltr">{{round(($sumTaraz / $sumCoherence), 0)}}</center></td>                        @else                            <td><center style="direction: ltr">{{round(($sumLesson), 0)}}</center></td>                            <td><center style="direction: ltr">{{round(($sumTaraz), 0)}}</center></td>                        @endif                        <td><center>{{$user->rank}}</center></td>                    </tr>                @endforeach            </table>        </div>    @endif    @if(isset($msg))        <center>            <p class="warning_color" style="margin-top: 50px">{{$msg}}</p>        </center>    @endif@stop@section('title')    خانه@stop